<template>
    <div class="addBookList">
      <div v-transfer-dom>
        <loading v-model="loading"></loading>
      </div>
      <div>
        <search
          placeholder="搜索"
          @on-result-click="resultClick"
          @on-change="getResult"
          @on-cancel="onCancel"
          :results="results"
          position="absolute"
          auto-scroll-to-top top="46px"
          @on-focus="onFocus"
        ></search>
      </div>
      <scroller lock-x :scrollbar-y="true" height="-90" ref="scrollerEvent">
        <div>
          <group v-for="(addBook, idx) in addrBookDataList" :key="idx" :title="addBook.groupLetter" :id="'addressBookReference' + addBook.groupLetter">
            <cell @click.native="goAddressBookDetail(item.zgh)"
                  v-for="(item, idx) in addBook.addBookLst" :key="idx">
              <span class="school-wx-icon" slot="icon" style="padding-right: 0;color: #2890d5;font-size: 24px;">&#xe846;</span>
              <span slot="after-title" class="vux-label">
                {{item.XM}}
                <div style="font-size: 12px;color: #999999;">{{item.lxdh}}</div>
              </span>
            </cell>
          </group>
        </div>
      </scroller>
      <aside class="letter-aside">
        <ul :class="seleLetter !== ''? 'asideBgColor' : ''"><!--v-on:swipedown.native="onSwipeDown()"  {{addBook.groupLetter}}-->
          <li v-for="(addBook, idx) in addrBookDataList" :key="idx" @click.stop.prevent="rightLetterReference(addBook.groupLetter)">
            {{addBook.groupLetter}}
          </li>
        </ul>
      </aside>
      <div class="addressBookMidLetter" v-if="seleLetter !== ''">
        {{seleLetter}}
      </div>
    </div>
</template>
