      },
      // 参数,中文字符串
      // 返回值:拼音首字母串数组
      makePy (str) {
        /*console.log(`str is ${str}`);*/
        if (typeof(str) !== "string") {
          throw new Error(-1, "函数makePy需要字符串类型参数!" );
        }
        let arrResult = []; //保存中间结果的数组
        for (let i=0; i < 1; i++){
         //获得unicode码
          let ch = str.charAt(i);
          // 检查该unicode码是否在处理范围之内,在则返回该码对映汉字的拼音首字母,不在则调用其它函数处理
          arrResult.push(this.checkCh(ch));
          /*console.log(`for str is ${arrResult}`);*/
        }
        //处理arrResult,返回所有可能的拼音首字母串数组
        return this.mkRslt(arrResult);
      },
      checkCh (ch) {
        let uni = ch.charCodeAt(0);
        // 如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数
        if (uni > 40869 || uni < 19968) {
          /*console.log(`checkCh ch is ${ch}`);*/
          return ch;
        }
        this.getNameFirstPY();
     //检查是否是多音字,是按多音字处理,不是就直接在strChineseFirstPY字符串中找对应的首字母
        return (this.oMultiDiff[uni] ? this.oMultiDiff[uni] : (this.strChineseFirstPY.charAt(uni-19968)));
      },
      mkRslt (arr){
        /*console.log(`mkRslt arr is ${arr}`);*/
        let arrRslt = [""];
        for (let i=0, len=arr.length; i<len; i++){
          /*console.log(`into for arr is ${arr}`);*/
          let str = arr[i];
          let strlen = str.length;
          /*console.log(strlen);*/
          if ( strlen === 1 ){
            /*console.log(`into if strlen===1 arr is ${arr}`);*/
            for (let k=0; k<arrRslt.length; k++){
              arrRslt[k] += str;
            }
          } else {
            /*console.log(`into else arr is ${arr}`);*/
            let tmpArr = arrRslt.slice(0);
            arrRslt = [];
            for (let k=0; k < 1; k++){
             //复制一个相同的arrRslt
              let tmp = tmpArr.slice(0);
              /*console.log(tmp);*/
              //把当前字符str[k]添加到每个元素末尾
              for (let j=0; j < tmp.length; j++){
                tmp[j] += str.charAt(k);
              }
              /*console.log(`into else for tmp is ${tmp}`);*/
            //把复制并修改后的数组连接到arrRslt上
              arrRslt = arrRslt.concat(tmp);
            }
          }
        }
        /*console.log(`mkRslt arrRslt finally is ${arrRslt}`);*/
        return arrRslt;
      },
  // 获取通讯录中详细列表 方法
      async getAddressBookDataList (){
        this.loading = true;
        let englishLetter = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('');
        let d = await this.QD.getData('/ComApi/PostObject', {apiName: 'AddressBookDataList'});
        if (d.status && d.status === "error") {
          this.QD.alert(`获取数据失败:${d.msg}`);
        }
        // 从接口中取出来的通讯录信息 赋值，在搜索中用到
        this.getAddrBookData = d;
        let addLetter = [];
        /*存放所有人的姓名，职工号，和姓名首字母*/
        for (let i=0; i<d.length; i++){
          let letter = this.makePy(d[i].XM).toString();
          addLetter.push({
            /*XM: d[i].XM,
            zgh: d[i].ZGH,*/
            letter: letter
          });
        }
        for (let j = 0; j < englishLetter.length; j++) {
          let groupLetter = ''; /*在group上显示的字母*/
          let addBookLst = []; // 姓名相同的首字母通讯录所有信息集合
          /*把姓名相同的首字母中的姓名、职工号和联系电话归为一类*/
          for (let k = 0; k < addLetter.length; k++) {
            if (addLetter[k].letter === englishLetter[j]) {
              groupLetter = englishLetter[j];
              addBookLst.push({
                XM: d[k].XM,
                zgh: d[k].ZGH,
                lxdh: d[k].LXDH
              });
            }
          }
          /*按姓名相同的首字母存放，首字母信息以及姓名、职工号和联系电话*/
          if (groupLetter !== '') {
            this.addrBookDataList.push({
              groupLetter: groupLetter,
              addBookLst: addBookLst
            });
          }
        }
        this.loading = false;
      },
      goAddressBookDetail (zgh) {
        this.$router.push({path: '/addressBookDetail', query: {addressBookzgh: zgh}});
      },
      // 点击选择搜索出来学生的事件
      resultClick (item) {
        let letter = this.makePy(item.xm).toString();
        // 相当于点击右边的字母索引
        this.rightLetterReference(letter);
      },
      // 在input标签中输入文字是发生的事件
      getResult (val) {
        this.results = val ? this.getResults(val) : [];
      },
      // 查找输入的姓名事件
      getResults (val) {
        let rs = [];
        // 根据输入的职工号或姓名查找教师
        for (let i=0; i<this.getAddrBookData.length; i++) {
          if (this.getAddrBookData[i].XM.indexOf(val) !== -1 || this.getAddrBookData[i].ZGH.indexOf(val) !== -1) {
            rs.push({
              title: this.getAddrBookData[i].XM + '(' + this.getAddrBookData[i].ZGH + ')',
              zgh: this.getAddrBookData[i].ZGH,
              xm: this.getAddrBookData[i].XM,
              other: i
            });
          }
        }
        return rs
      },
      // 点击搜索中的取消事件
      onCancel () {
        // 删除搜索中的老师信息，回到登录者教师的信息
        // 相当于点击右边的字母索引
        this.rightLetterReference(this.addrBookDataList[0].groupLetter);
      },
      // input 获得焦点事件
      onFocus () {
        let rs = [];
        if (this.getAddrBookData.length !== 0) {
          for (let i = 0; i < this.getAddrBookData.length; i++) {
            rs.push({
              title: this.getAddrBookData[i].XM + "(" + this.getAddrBookData[i].ZGH + ")",
              zgh: this.getAddrBookData[i].ZGH,
              xm: this.getAddrBookData[i].XM,
              other: i
            });
          }
        }
        this.results = rs;
      }
    },
    created () {
      this.getAddressBookDataList();
    },
    data () {
      return {
        loading: false,
        letterLst: [], // 不同的首字母
        getAddrBookData: [], // 从接口中取出来的通讯录信息
        addrBookDataList: [], // 通讯录中所有信息
        oMultiDiff: {},
        strChineseFirstPY: '',
        seleLetter: '', // 选中的首字母
        results: [] // 搜索出现的学生姓名
      }
    }
  }
</script>
